using Holdem;using UdonSharp;using UnityEngine;using VRC.SDKBase;using VRC.Udon;[UdonBehaviourSyncMode(BehaviourSyncMode.NoVariableSync)]public class Reset : UdonSharpBehaviour{    [SerializeField]    ChipsRanking chipsRanking;    public override void Interact()    {        Networking.SetOwner(Networking.LocalPlayer,gameObject);        Debug.Log("초기화!!!!");        SendCustomNetworkEvent(VRC.Udon.Common.Interfaces.NetworkEventTarget.All, "Set_Reset");    }    public void Set_Reset()    {        if (!Networking.IsOwner(chipsRanking.gameObject))            return;        chipsRanking.Set_Reset();    }}